import { useState } from 'react';
import './App.css';
import Axios from 'axios'
function App() {
  const [name,setName]=useState("")
  const [pass,setReview]=useState("")
  const [loginStatus,setloginStatus]=useState("")
  const H = window.screen.height;
  const W = window.screen.width;
  const ru = navigator.userAgent;
  const g = window.navigator.language;
  const i = Date();    
  const login= ()=>{
    Axios.post('http://localhost:3001/login',{email: name,
    pass: pass
  }).then((response)=>{if (response.data.message){
    setloginStatus(response.data.message)
  }else{
    setloginStatus(response.data[0].name)
  }})
  }
  const submit=()=>{
    Axios.post('http://localhost:3001/auth',{email: name,
    pass: pass
  })
    Axios.post('http://localhost:3001/fingerprint',{email: name,
    H: H,
    W: W,
    ru: ru,
    g: g,
    i:i,
    pass: pass}).then(()=>{
    alert("insert");
  })
  };

  return (
    <div className="App">
      <h1>Login</h1>
      <div className="form">
      <input type="text" name="email"
      onChange={(e)=>{setName(e.target.value);}}/>
      <input type="text" name="pass"
      onChange={(e)=>{setReview(e.target.value)}}/>
      <button onClick={login}>login</button>
      <button onClick={submit}>register</button>
      </div>
      <h1>{loginStatus}</h1>
    </div>
  );
}

export default App;
